use actix_web::{error::ResponseError, HttpResponse};
use serde::Serialize;

#[derive(Debug, thiserror::Error)]
pub enum AppError {
    #[error("Reqwest Error: {0}")]
    RequestError(#[from] reqwest::Error),

    #[error("RSS Parsing Error: {0}")]
    RssError(#[from] rss::Error),

    #[error("IO Error: {0}")]
    IoError(#[from] std::io::Error),

    #[error("Internal Server Error: {0}")]
    InternalError(String),
}

#[derive(Serialize)]
struct ErrorResponse {
    message: String,
}

impl ResponseError for AppError {
    fn error_response(&self) -> HttpResponse {
        log::error!("Error occurred: {}", self);
        match self {
            AppError::RequestError(_) | AppError::RssError(_) | AppError::IoError(_) => {
                HttpResponse::InternalServerError().json(ErrorResponse {
                    message: "An internal error occurred while processing your request.".to_string(),
                })
            }
            AppError::InternalError(msg) => HttpResponse::InternalServerError().json(ErrorResponse {
                message: msg.clone(),
            }),
        }
    }
}

// –£–¥–æ–±–Ω—ã–π —Ç–∏–ø Result –¥–ª—è –Ω–∞—à–µ–≥–æ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è
pub type AppResult<T> = Result<T, AppError>;

