use actix_web::{test, web, App, http};
use backend::config::config_services;
use backend::errors::{AppError, AppResult};
use backend::telegram::{models::Post, services::RssFetcher};
use async_trait::async_trait;
use bytes::Bytes;
use std::sync::Arc;

#[derive(Clone)]
struct MockSuccessFetcher;

#[async_trait]
impl RssFetcher for MockSuccessFetcher {
    async fn fetch_rss_content(&self, _url: &str) -> AppResult<Bytes> {
        let fake_rss = r#"<rss version="2.0"><channel><title>API Test</title><item><title>API Post</title><link>url</link><description>desc</description></item></channel></rss>"#;
        Ok(Bytes::from(fake_rss))
    }
}

#[derive(Clone)]
struct MockErrorFetcher;

#[async_trait]
impl RssFetcher for MockErrorFetcher {
    async fn fetch_rss_content(&self, _url: &str) -> AppResult<Bytes> {
        Err(AppError::InternalError("mock network error".into()))
    }
}

#[actix_web::test]
async fn test_get_telegram_posts_api_success() {
    let mock_fetcher = Arc::new(MockSuccessFetcher);
    let app = test::init_service(
        App::new()
            .app_data(web::Data::from(mock_fetcher as Arc<dyn RssFetcher + Send + Sync>))
            .configure(config_services),
    ).await;

    let req = test::TestRequest::get().uri("/api/telegram").to_request();
    let resp = test::call_service(&app, req).await;

    // –°–Ω–∞—á–∞–ª–∞ –ø—Ä–æ–≤–µ—Ä—è–µ–º —Å—Ç–∞—Ç—É—Å
    assert_eq!(resp.status(), http::StatusCode::OK);

    // –¢–µ–ø–µ—Ä—å —á–∏—Ç–∞–µ–º —Ç–µ–ª–æ
    let posts: Vec<Post> = test::read_body_json(resp).await;
    assert_eq!(posts.len(), 1);
    assert_eq!(posts[0].title, "API Post");
}

#[actix_web::test]
async fn test_get_telegram_posts_api_error_fallback() {
    let mock_fetcher = Arc::new(MockErrorFetcher);
    let app = test::init_service(
        App::new()
            .app_data(web::Data::from(mock_fetcher as Arc<dyn RssFetcher + Send + Sync>))
            .configure(config_services),
    ).await;

    let req = test::TestRequest::get().uri("/api/telegram").to_request();
    let resp = test::call_service(&app, req).await;

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Å—Ç–∞—Ç—É—Å 200 (—Ç.–∫. –º—ã –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –æ—à–∏–±–∫—É)
    assert_eq!(resp.status(), http::StatusCode::OK, "Status should be 200 OK on fallback");

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Ç–µ–ª–æ - –ø—É—Å—Ç–æ–π –º–∞—Å—Å–∏–≤
    let posts: Vec<Post> = test::read_body_json(resp).await;
    assert_eq!(posts.len(), 0, "Should return an empty list on service error");
}

