'use client'

import { Button } from "@/components/ui/button"
import { useLocale } from "next-intl"
import { locale as LocaleType } from "@/types/i18n";
import { usePathname, useRouter } from "next/navigation"; 
import { useTransition } from 'react';
import { locales } from '@/types/i18n'; // –î–ª—è –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è –Ω–∞ —Å–ª–µ–¥—É—é—â—É—é –ª–æ–∫–∞–ª—å

export function LanguageSwitcher() {
  const currentLocale = useLocale() as LocaleType;
  const router = useRouter();
  const currentPathname = usePathname(); // –≠—Ç–æ –±—É–¥–µ—Ç –ü–û–õ–ù–´–ô pathname, –≤–∫–ª—é—á–∞—è –ª–æ–∫–∞–ª—å (–Ω–∞–ø—Ä–∏–º–µ—Ä, /en/about)
  const [isPending, startTransition] = useTransition();

  const switchLocale = (newLocale: LocaleType) => {
    if (newLocale === currentLocale || isPending) return;

    startTransition(() => {
      // –ü—Ä–∏ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–∏ —Ö—É–∫–æ–≤ –∏–∑ next/navigation, –Ω—É–∂–Ω–æ –≤—Ä—É—á–Ω—É—é —É–±–∏—Ä–∞—Ç—å —Å—Ç–∞—Ä—ã–π –ø—Ä–µ—Ñ–∏–∫—Å –ª–æ–∫–∞–ª–∏
      // –∏ –¥–æ–±–∞–≤–ª—è—Ç—å –Ω–æ–≤—ã–π, –µ—Å–ª–∏ –æ–Ω –Ω–µ —Å–æ–≤–ø–∞–¥–∞–µ—Ç —Å —Ç–µ–∫—É—â–∏–º.
      // –ò–ª–∏, –µ—Å–ª–∏ next-intl middleware –Ω–∞—Å—Ç—Ä–æ–µ–Ω –ø—Ä–∞–≤–∏–ª—å–Ω–æ, –æ–Ω —Å–∞–º –º–æ–∂–µ—Ç —Å–ø—Ä–∞–≤–∏—Ç—å—Å—è —Å —Ä–µ–¥–∏—Ä–µ–∫—Ç–æ–º.

      // –ü—Ä–æ—Å—Ç–æ–π —Å–ø–æ—Å–æ–±: router.replace —Å –Ω–æ–≤—ã–º URL
      // –£–±–∏—Ä–∞–µ–º —Ç–µ–∫—É—â–∏–π –ø—Ä–µ—Ñ–∏–∫—Å –ª–æ–∫–∞–ª–∏ –∏–∑ currentPathname, –µ—Å–ª–∏ –æ–Ω –µ—Å—Ç—å
      let pathnameWithoutLocale = currentPathname;
      if (currentPathname.startsWith(`/${currentLocale}`)) {
        pathnameWithoutLocale = currentPathname.substring(`/${currentLocale}`.length);
        if (pathnameWithoutLocale === '') pathnameWithoutLocale = '/'; // –¥–ª—è –∫–æ—Ä–Ω–µ–≤–æ–≥–æ –ø—É—Ç–∏
      }

      router.replace(`/${newLocale}${pathnameWithoutLocale === '/' && newLocale !== 'en' ? '' : pathnameWithoutLocale}`);
      // router.refresh(); // –ú–æ–∂–µ—Ç –ø–æ–Ω–∞–¥–æ–±–∏—Ç—å—Å—è –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –Ω–∞ —Å–µ—Ä–≤–µ—Ä–µ
    });
  }

  const getNextLocale = () => {
    const currentIndex = locales.indexOf(currentLocale);
    return locales[(currentIndex + 1) % locales.length];
  }

  return (
    <Button 
      className="rounded-full" 
      // onClick={() => switchLocale(currentLocale === 'en' ? 'ru' : 'en')} 
      onClick={() => switchLocale(getNextLocale())}
      variant="outline" 
      size="icon"
      disabled={isPending}
    >
      {currentLocale.toUpperCase() === 'EN' ? 'RU' : 'EN'}
    </Button>
  )
}

'use client'

import { Button } from "@/components/ui/button"
import Image from "next/image"
import { useState, useRef, useEffect } from "react"

const MUSIC_VOLLUME_CHENGE = 0.03
const MUSIC_VOLLUME_MAX = 0.3

const musicTracks = [
  "breezehome.mp3",
  "getting better.mp3",
  "soul gem.mp3",
].map((name) => `/music/${name}`)

export function MusicButton() {
  const [isPlaying, setIsPlaying] = useState(false)
  const [fadeOut, setFadeOut] = useState(false)
  const [fadeIn, setFadeIn] = useState(false)
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0)
  const audioRef = useRef<HTMLAudioElement | null>(null)

  useEffect(() => {
    let fadeInterval: NodeJS.Timeout | null = null

    if (fadeOut && audioRef.current) {
      fadeInterval = setInterval(() => {
        if (audioRef.current && audioRef.current.volume > 0) {
          audioRef.current.volume = Math.max(audioRef.current.volume - MUSIC_VOLLUME_CHENGE, 0)
        } else {
          clearInterval(fadeInterval!)
          setIsPlaying(false)
          setFadeOut(false)
          audioRef.current!.pause()
        }
      }, 100)
    }

    return () => {
      if (fadeInterval) clearInterval(fadeInterval)
    }
  }, [fadeOut])

  useEffect(() => {
    let fadeInInterval: NodeJS.Timeout | null = null

    if (fadeIn && audioRef.current) {
      fadeInInterval = setInterval(() => {
        if (audioRef.current && audioRef.current.volume < 1) {
          audioRef.current.volume = Math.min(audioRef.current.volume + MUSIC_VOLLUME_CHENGE, MUSIC_VOLLUME_MAX)
        } else {
          clearInterval(fadeInInterval!)
        }
      }, 100)
    }

    return () => {
      if (fadeInInterval) clearInterval(fadeInInterval)
    }
  }, [fadeIn])

  const playNextTrack = () => {
    const nextTrackIndex = (currentTrackIndex + 1) % musicTracks.length
    setCurrentTrackIndex(nextTrackIndex)

    if (audioRef.current) {
      audioRef.current.src = musicTracks[nextTrackIndex]
      audioRef.current.play()
      setIsPlaying(true)
      setFadeIn(true)
      setFadeOut(false)
    }
  }

  const toggleMusic = () => {
    if (audioRef.current) {
      if (isPlaying) {
        setFadeIn(false)
        setFadeOut(true)
      } else {
        audioRef.current.play()
        setIsPlaying(true)
        setFadeIn(true)
        setFadeOut(false)
      }
    }
  }

  const onTrackEnded = () => {
    playNextTrack()
  }

  return (
    <div>
      <Button className="dark:bg-white dark:border-white dark:invert rounded-full" onClick={toggleMusic} variant="outline" size="icon">
        <Image src={"/music.png"} width={20} height={20} alt={"Music"} />
      </Button>

      <audio
        ref={audioRef}
        src={musicTracks[currentTrackIndex]}
        preload="auto"
        onEnded={onTrackEnded}
      />
    </div>
  )
}

