export class HttpError extends Error {
  status: number;
  data: any;

  constructor(status: number, data: any, message?: string) {
    super(message || `Request failed with status ${status}`);
    this.name = 'HttpError';
    this.status = status;
    this.data = data; // –¢–µ–ª–æ –æ—Ç–≤–µ—Ç–∞ —Å –æ—à–∏–±–∫–æ–π –æ—Ç –±—ç–∫–µ–Ω–¥–∞
  }
}

const BACKEND_URL = process.env.RUST_BACKEND_URL || 'http://localhost:8080';
console.log('BACKEND_URL', BACKEND_URL);

type HttpMethod = 'GET' | 'POST' | 'PUT' | 'DELETE' | 'PATCH';

interface RequestOptions {
  headers?: HeadersInit;
  body?: BodyInit | Record<string, any>;
  next?: {
    revalidate?: number | false;
    tags?: string[];
  };
}

/**
 * –£–ª—É—á—à–µ–Ω–Ω—ã–π –∏ –Ω–∞–¥–µ–∂–Ω—ã–π –±–∏–ª–¥–µ—Ä –∑–∞–ø—Ä–æ—Å–æ–≤ –∫ –±—ç–∫–µ–Ω–¥—É.
 * @template T - –û–∂–∏–¥–∞–µ–º—ã–π —Ç–∏–ø –¥–∞–Ω–Ω—ã—Ö –≤ —É—Å–ø–µ—à–Ω–æ–º –æ—Ç–≤–µ—Ç–µ.
 * @param endpoint - –ü—É—Ç—å –∫ API (–Ω–∞–ø—Ä–∏–º–µ—Ä, '/api/telegram').
 * @param method - HTTP-–º–µ—Ç–æ–¥.
 * @param options - –û–±—ä–µ–∫—Ç —Å –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–º–∏ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏: headers, body, next (–¥–ª—è –∫–µ—à–∞).
 * @returns - –ü—Ä–æ–º–∏—Å —Å –¥–∞–Ω–Ω—ã–º–∏ —Ç–∏–ø–∞ T.
 * @throws {HttpError} - –í —Å–ª—É—á–∞–µ –æ—à–∏–±–∫–∏ —Å–µ—Ç–∏ –∏–ª–∏ —Å—Ç–∞—Ç—É—Å–∞ –æ—Ç–≤–µ—Ç–∞, –Ω–µ —Ä–∞–≤–Ω–æ–≥–æ 2xx.
 */
export const requestToBackend = async <T = unknown>(
  endpoint: string,
  method: HttpMethod,
  options: RequestOptions = {}
): Promise<T> => {
  const url = new URL(endpoint, BACKEND_URL);

  const config: RequestInit = {
    method,
    next: options.next, 
  };

  const headers = new Headers(options.headers);

  if (options.body && method !== 'GET') {
    if (typeof options.body === 'object' && !(options.body instanceof FormData)) {
      config.body = JSON.stringify(options.body);
      if (!headers.has('Content-Type')) {
        headers.set('Content-Type', 'application/json');
      }
    } else {
      config.body = options.body as BodyInit;
    }
  }

  config.headers = headers;

  try {
    const response = await fetch(url.toString(), config);

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ message: 'Backend returned non-JSON error response' }));
      throw new HttpError(response.status, errorData);
    }

    if (response.status === 204) {
      return {} as T;
    }

    return response.json();

  } catch (error) {
    if (error instanceof HttpError) {
      throw error;
    }

    console.error('Network or fetch error:', error);
    throw new Error('Network error or failed to fetch');
  }
};

