nginx.conf.local:
events {
    worker_connections 1024;
}

http {
    upstream frontend_local_upstream { # –ï—Å–ª–∏ –ø—Ä–µ–¥–ø–æ—á–∏—Ç–∞–µ—Ç–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å upstream
        server website_frontend_local:3000;        # 'frontend' - –∏–º—è —Å–µ—Ä–≤–∏—Å–∞ –∏–∑ docker-compose.yml
    }

    server {
        listen 80;
        server_name localhost;
        return 301 https://$host$request_uri/;
    }

    server {
        listen 443 ssl;
        server_name localhost;

        ssl_certificate /certs/certificate.pem;
        ssl_certificate_key /certs/private-key.pem;

        location / {
            proxy_pass http://website_frontend_local:3000;

            proxy_set_header Host $host;
            proxy_set_header X-Real-IP $remote_addr;
            proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
            proxy_set_header X-Forwarded-Proto $scheme;
            proxy_http_version 1.1;
            proxy_set_header Upgrade $http_upgrade;
            proxy_set_header Connection "upgrade";
        }

        location /api/ {
            proxy_pass http://website_backend_local:8080;

            proxy_set_header Host $host;
            proxy_set_header X-Real-IP $remote_addr;
            proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
            proxy_set_header X-Forwarded-Proto $scheme;
        }
    }
}

