        server frontend:3000;       # 'frontend' - –∏–º—è —Å–µ—Ä–≤–∏—Å–∞ –∏–∑ docker-compose.yml
    }

    server {
        listen 80;
        server_name diametrfq.ru; # –í–∞—à —Ä–µ–∞–ª—å–Ω—ã–π –¥–æ–º–µ–Ω
        return 301 https://$host$request_uri;
    }

    server {
        listen 443 ssl http2; # –î–æ–±–∞–≤–∏–ª http2 –¥–ª—è –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏
        server_name diametrfq.ru; # –í–∞—à —Ä–µ–∞–ª—å–Ω—ã–π –¥–æ–º–µ–Ω

        ssl_certificate /etc/letsencrypt/live/diametrfq.ru/fullchain.pem;
        ssl_certificate_key /etc/letsencrypt/live/diametrfq.ru/privkey.pem;

